    public static byte[] patternKalos(MapleClient c, ArrayList<Point> positions, int type, int subType, int generic1, int generic2) {
        MaplePacketLittleEndianWriter mplew = new MaplePacketLittleEndianWriter();
        mplew.writeShort(SendPacketOpcode.KALOS_DO_SKILL.getValue());

        mplew.writeInt(type);
        
        switch (type) {
            case 1: {
                mplew.writeInt(subType);
                
                switch (subType) {
                    case 1: { // 포격 전투기 (드론) 소환 
                        mplew.writeInt(60000); // 게이지 남은 시간
                        mplew.writeInt(150000);

                        int size = positions.size();
                        int index = 0;

                        mplew.writeInt(size);
                        for (Point position : positions) {
                            mplew.writeInt(index++);
                            mplew.writeInt(position.x);
                            mplew.writeInt(position.y);
                        }

                        mplew.writeInt(size);
                        for (int i = 0; i < size; i++) {
                            mplew.writeInt(i);
                            mplew.write(0);
                        }
                        
                        mplew.writeInt(size);
                        for (int i = 0; i < size; i++) {
                            mplew.writeInt(i);
                            mplew.write(0);
                        }
                        
                        break;
                    }
                    case 2: { // 게이지 시간 초기화
                        mplew.writeInt(60000); // 게이지 남은 시간
                        
                        break;
                    }
                    case 3: {
                        mplew.writeInt(150000);
                        
                        break;
                    }
                    case 7: { // 게이지 시간 초기화 (풀 스택)
                        mplew.writeInt(60000); // 게이지 남은 시간
                        
                        break;
                    }
                }
                
                break;
            }
            case 3: {
                mplew.writeInt(subType);
                
                switch (subType) {
                    case 9: { // 포격 전투기 비활성화
                        mplew.writeInt(positions.get(0).x);
                        mplew.writeInt(positions.get(0).y);
                        
                        break;
                    }
                    case 10: { // 포격 전투기 활성화 & 공격 (generic2 == 1 :: 활성화 / generic2 == 0 :: 공격)
                        mplew.writeInt(generic1);
                        
                        mplew.writeInt(330);
                        mplew.writeInt(8881011);
                        mplew.writeInt(4);
                        mplew.writeInt(150);
                        mplew.writeInt(10000);
                        mplew.writeInt(200);
                        
                        mplew.writeInt(0);
                        mplew.writeInt(0);
                        mplew.writeInt(0);
                        mplew.writeInt(0);
                        mplew.writeInt(0);
                        mplew.writeInt(0);
                        
                        mplew.writeInt(2);
                        mplew.writeInt(2);
                        
                        if (generic2 != 0) {
                            mplew.writeInt(1);
                            mplew.writeInt(0);
                        }
                        
                        mplew.writeShort(0);
                        
                        int size = positions.size();
                        for (int i = 0; i < size; i++) {
                            mplew.writeInt(positions.get(i).x);
                            mplew.writeInt(positions.get(i).y);
                        }
                        
                        break;
                    }
                    case 12: { // 포격 전투기 스택 차감 (파티원 당 2 스택)
                        mplew.writeInt(generic1);
                        
                        mplew.writeInt(generic2);
                        mplew.writeInt(generic2);
                        
                        break;
                    }
                    case 13: { // 오디움의 구체 비활성화
                        break;
                    }
                    case 14: { // 오디움의 구체 활성화
                        mplew.writeInt(5);
                        
                        mplew.write(0);
                        mplew.write(1);
                        
                        mplew.writeInt(500);
                        mplew.writeInt(720);
                        mplew.writeInt(8881011);
                        mplew.writeInt(5);
                        
                        mplew.writeInt(0);
                        mplew.writeInt(0);
                        mplew.writeInt(0);
                        mplew.writeInt(0);
                        
                        mplew.writeInt(2);
                        
                        int size = positions.size();
                        
                        mplew.writeInt(size);
                        for (Point position : positions) {
                            mplew.writeInt(position.x);
                            mplew.writeInt(position.y);
                        }
                        
                        break;
                    }
                    case 15: { // 오디움의 구체 스택 차감 (파티원 당 2 스택)
                        mplew.writeInt(generic1);
                        
                        mplew.writeInt(generic2);
                        mplew.writeInt(generic2);
                        
                        break;
                    }
                    case 16: { // 구속의 눈 비활성화
                        break;
                    }
                    case 17: { // 구속의 눈 활성화
                        mplew.writeInt(8881011); // 몬스터 코드
                        mplew.writeInt(2);
                        mplew.writeInt(100);
                        mplew.writeInt(360);
                        mplew.writeInt(15000);
                        
                        mplew.writeInt(0);
                        mplew.writeInt(0);
                        mplew.writeInt(0);
                        mplew.writeInt(0);
                        mplew.writeInt(0);
                        
                        mplew.writeInt(3);
                        mplew.write(0);
                        
                        break;
                    }
                    case 18: { // 구속의 눈 공격
                        mplew.writeInt(generic1);
                        
                        int size = positions.size();
                        
                        mplew.writeInt(size);
                        for (Point position : positions) {
                            mplew.writeInt(position.x);
                            mplew.writeInt(position.y);
                        }
                        
                        break;
                    }
                    case 19: { // 심연의 눈 스택 차감 (파티원 당 3 스택)
                        mplew.writeInt(c.getPlayer().getId());
                        
                        mplew.writeInt(generic1);
                        
                        break;
                    }
                    case 20: { // 심연의 눈 비활성화
                        break;
                    }
                    case 21: { // 심연의 눈 활성화
                        mplew.writeInt(8881011);
                        mplew.writeInt(3);
                        mplew.writeInt(15000);
                        mplew.writeInt(500);

                        mplew.writeInt(0);
                        mplew.writeInt(0);
                        mplew.writeInt(0);
                        mplew.writeInt(0);

                        mplew.writeInt(3);

                        mplew.write(0);
                        
                        break;
                    }
                    case 22: { // 심연의 눈 공격
                        mplew.writeInt(generic1);
                        
                        int repeat = 3;
                        
                        mplew.writeInt(repeat);
                        for (int i = 0; i < repeat; i++) {
                            mplew.writeInt(i == 0 ? 17 : i == 1 ? 23 : 29);
                            mplew.writeInt(1);
                            mplew.writeInt(c.getPlayer().getId());
                            mplew.writeInt(0);
                        }
                        
                        break;
                    }
                    case 23: { // 심연의 눈 스택 차감 (파티원 당 3 스택)
                        mplew.writeInt(c.getPlayer().getId());
                        
                        mplew.writeInt(generic1);
                        
                        break;
                    }
                    case 24: {
                        mplew.writeInt(generic1);
                        
                        mplew.writeInt(c.getPlayer().getId());
                        
                        break;
                    }
                }
                
                break;
            }
            case 5: {
                mplew.writeInt(subType);
                
                switch (subType) {
                    case 25: {
                        mplew.writeInt(2000);
                        mplew.writeInt(20);
                        mplew.writeInt(100);
                        mplew.writeInt(100);
                        
                        break;
                    }
                    case 26: {
                        mplew.writeInt(10000);
                        
                        int size = positions.size();
                        
                        mplew.writeInt(size);
                        for (Point position : positions) {
                            mplew.writeInt(position.x);
                            mplew.writeInt(position.y);
                        }
                        
                        break;
                    }
                    case 27: {
                        mplew.writeInt(10000);
                        mplew.writeInt(1780);
                        
                        int size = positions.size();
                        
                        mplew.writeInt(size);
                        for (Point position : positions) {
                            mplew.writeInt(position.x);
                            mplew.writeInt(position.y);
                        }
                        
                        break;
                    }
                    case 28: { // 칼로스의 의지 화살
                        mplew.writeInt(generic1);
                        
                        mplew.writeInt(c.getPlayer().getId());
                        
                        mplew.writeInt(1500);
                        mplew.writeInt(positions.get(0).x);
                        mplew.writeInt(positions.get(0).y);
                        mplew.writeInt(generic2);
                        
                        break;
                    }
                    case 29: { // 위 새틀라이트 적중
                        mplew.writeInt(c.getPlayer().getId());
                        
                        mplew.writeInt(generic1);
                        
                        break;
                    }
                    case 30: { // 아래 새틀라이트 적중
                        mplew.writeInt(c.getPlayer().getId());
                        
                        mplew.writeInt(generic1);
                        
                        break;
                    }
                }
                
                break;
            }
        }
        
        return mplew.getPacket();
    }
